  if (loading) return <LoadingSpinner />;
  if (!statistics) return null;

  const { statistics: stats, streak, weeklySummary, badges } = statistics;

  return (
    <div className="space-y-6" data-testid="statistics-dashboard">
      {/* Header Stats */}
